import React, { useState } from 'react';
import './Cuadrito.css';

function Cuadrito(props) {
    const [cuadritoColor, setCuadritoColor] = useState("white");
    const [oldColor, setOldColor] = useState(cuadritoColor);
    const [pintado, setPintado] = useState(false);
    let bandera = false;

    function onMouseDown(){
        bandera = true;
    }

    function applyColor() {
        setCuadritoColor(props.selectedColor);
        setPintado(true);
    }
    function changeColorOnHover() {
        setOldColor(cuadritoColor);
        setCuadritoColor(props.selectedColor);
    }

    function resetColor() {
        if (!bandera) {
            if (!pintado)
                setCuadritoColor(oldColor);

            setPintado(false);
        }
    }

    return <div className="cuadrito" onClick={applyColor} onMouseDown={onMouseDown} onMouseEnter={changeColorOnHover} style={{ backgroundColor: cuadritoColor }} onMouseLeave={resetColor}></div>
}

export default Cuadrito;
